import { ApexOptions } from 'apexcharts';
import Chart from 'react-apexcharts';
import { Card, Dropdown } from 'react-bootstrap';

const StatisticsChart = () => {
    const apexOpts: ApexOptions = {
        chart: {
            type: 'bar',
            toolbar: {
                show: false,
            },
        },
        plotOptions: {
            bar: {
                columnWidth: '20%',
            },
        },
        dataLabels: {
            enabled: false,
        },
        stroke: {
            show: false,
        },
        xaxis: {
            categories: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5', 'Day 6', 'Day 7', 'Day 8', 'Day 9', 'Day 10'],
            axisBorder: {
                show: false,
            },
            axisTicks: {
                show: false,
            },
            labels: {
                style: {
                    colors: '#adb5bd',
                },
            },
        },
        yaxis: [
            {
                labels: {
                    style: {
                        colors: '#adb5bd',
                    },
                    formatter: (value: number) => {
                        if (value === -1) {
                            return 'Low';
                        } else if (value === 0) {
                            return 'Normal';
                        } else if (value === 1) {
                            return 'High';
                        }
                        return '';
                    },
                },
                tickAmount: 3,
                min: -1,
                max: 1,
                forceNiceScale: true,
            },
        ],
        grid: {
            show: false,
            padding: {
                top: 0,
                right: 0,
                bottom: 0,
                left: 0,
            },
        },
        fill: {
            colors: ['#ff0000', '#188ae2'], // Red for Low, Blue for Normal
        },
        tooltip: {
            theme: 'dark',
        },
    };

    const getRandomValue = () => {
        const range = Math.floor(Math.random() * 3) - 1; // Generate random number between -1, 0, 1
        return range; // Return the random variation directly
    };

    const apexData = [
        {
            name: '10 Day Overview',
            data: Array.from({ length: 10 }, () => getRandomValue()), // Generate 10 random values
        },
    ];

    return (
        <Card>
            <Card.Body>
                <Dropdown className="float-end" align="end">
                    <Dropdown.Toggle as="a" className="cursor-pointer card-drop">
                        <i className="mdi mdi-dots-vertical"></i>
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        <Dropdown.Item>Action</Dropdown.Item>
                        <Dropdown.Item>Another Action</Dropdown.Item>
                        <Dropdown.Item>Something Else</Dropdown.Item>
                        <Dropdown.Item>Separated link</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>

                <h4 className="header-title mt-0">10 Day Overview</h4>

                <div dir="ltr">
                    <Chart options={apexOpts} series={apexData} type="bar" height={268} className="apex-charts mt-2" />
                </div>
            </Card.Body>
        </Card>
    );
};




export default StatisticsChart;
