import React, { useState } from 'react';
import { Card, Dropdown, Button } from 'react-bootstrap';

const StatisticsChart = () => {
  const [scanning, setScanning] = useState(false);

  const handleMeasureNowClick = () => {
    setScanning(true);
    setTimeout(() => {
      setScanning(false);
    }, 3000);
  };

  return (
    <Card>
      <Card.Body>
        <Dropdown className="float-end" align="end">
          <Dropdown.Toggle as="a" className="cursor-pointer card-drop">
            <i className="mdi mdi-dots-vertical"></i>
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item>Action</Dropdown.Item>
            <Dropdown.Item>Another Action</Dropdown.Item>
            <Dropdown.Item>Something Else</Dropdown.Item>
            <Dropdown.Item>Separated link</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <h4 className="header-title mt-0">10 Day Overview</h4>

        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ flex: '1', marginRight: '10px' }}>
            <Button onClick={handleMeasureNowClick} disabled={scanning} style={{ backgroundColor: '#ff0000' }}>
              {scanning ? 'Scanning...' : 'Measure Now'}
            </Button>
          </div>

          <div style={{ flex: '1', backgroundColor: '#ff0000', height: '28px' }}></div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default StatisticsChart;
